                fadeAdj = "0." + (12 - f);
                harvest1.setOpacity(fadeAdj);
                harvest2.setOpacity(fadeAdj);
                harvest3.setOpacity(fadeAdj);
                harvest4.setOpacity(fadeAdj);
                harvest10.setOpacity(fadeAdj);
