                if (collectItems.digUpSells[i].who == 'Mayor') { mayorReady = 1; }
               else if (collectItems.digUpSells[i].who == 'Friar') { friarReady = 1; }
                else if (collectItems.digUpSells[i].who == 'Sara') { saraReady = 1; }
